def top_k ( lst , k ) :
    results = [ ]
    counter = 0
    while ( counter < k ) :
        max_value = lst [ 0 ]
        max_index = 0
        for i in range ( 1 , len ( lst ) ) :
            if ( lst [ i ] > max_value ) :
                max_value = lst [ i ]
                max_index = i
        results . append ( lst . pop ( max_index ) )
        counter += 1
    return results
